import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import axios from 'axios';
import "./map.css"

mapboxgl.accessToken = 'pk.eyJ1IjoiemFpZGgiLCJhIjoiY2xjZDZwOThyMGF3aTN3cHU5bjhvNmgweiJ9.uoMkvDXyEXWsyDGDFrbgWQ';

export default function Map() {

    // const mapContainer = useRef(null);
    // const map = useRef(null);
    // const [lng, setLng] = useState(-70.9);
    // const [lat, setLat] = useState(42.35);
    // const [zoom, setZoom] = useState(9);

    // useEffect(() => {
    //     if (map.current) return; // initialize map only once
    //     map.current = new mapboxgl.Map({
    //       container: mapContainer.current,
    //       style: 'mapbox://styles/mapbox/streets-v12',
    //       center: [lng, lat],
    //       zoom: zoom
    //     });
    //   });

      const [address, setAddress] = useState('');
      const [suggestions, setSuggestions] = useState([]);
      const [longitude, setLongitude] = useState(null);
      const [latitude, setLatitude] = useState(null);
    
      async function handleSubmit(event) {
        event.preventDefault();
    
        // Make a request to the Google Maps Geocoding API
        const response = await axios.get('https://maps.googleapis.com/maps/api/geocode/json', {
          params: {
            address: address,
            key: 'AIzaSyBir9Dolcj_TVFTaASOvlnBOH3rbN3EPaU'
          }
        });
        console.log(response)
        // Store the suggestions from the API
        setSuggestions(response.data.results);
      }
    
      function handleSuggestionSelect(suggestion) {
        // Store the longitude and latitude of the suggestion
        setLongitude(suggestion.geometry.location.lng);
        setLatitude(suggestion.geometry.location.lat);
    
        // Clear the suggestions
        setSuggestions([]);
      }
      return (
    //    <div className="mainContainer" onSubmit={handleSubmit}>
    //          <form className="formField">
    //          <input type="text" id="address" name="address" value={address} onChange={event => setAddress(event.target.value)} placeholder="Enter an address" />
    //                  <button type="submit">Search</button>
    //</form>
    //      <div ref={mapContainer} className="map-container" />
    //    </div>
    <form onSubmit={handleSubmit}>
    <label htmlFor="address">Address:</label><br />
    <input type="text" id="address" name="address" value={address} onChange={event => setAddress(event.target.value)} placeholder="Enter an address" />
    <button type="submit">Search</button>
    {suggestions.length > 0 && (
        <ul>
          {suggestions.map(suggestion => (
            <li key={suggestion.place_id} onClick={() => handleSuggestionSelect(suggestion)}>
              {suggestion.formatted_address}
            </li>
          ))}
        </ul>
    )}
  </form>
      );
}
