import React, { useState } from 'react';
import axios from 'axios';
import './test.css';

function GeocodingForm() {
  const [address, setAddress] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [longitude, setLongitude] = useState(null);
  const [latitude, setLatitude] = useState(null);

  async function handleSubmit(event) {
    event.preventDefault();

    // Make a request to the PositionStack Geocoding API
    const response = await axios.get('http://api.positionstack.com/v1/forward', {
      params: {
        query: address,
        access_key: 'YOUR_API_KEY'
      }
    });

    // Get the suggestions from the API
    const suggestions = response.data.data;

    // Update the state with the suggestions
    setSuggestions(suggestions);
  }

  function handleSuggestionClick(suggestion) {
    // Store the longitude and latitude of the suggestion
    setLongitude(suggestion.longitude);
    setLatitude(suggestion.latitude);

    // Clear the address and suggestions
    setAddress('');
    setSuggestions([]);
  }

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="address">Address:</label><br />
      <input type="text" id="address" name="address" value={address} onChange={event => setAddress(event.target.value)} placeholder="Enter an address" />
      <button type="submit">Search</button>
      {suggestions.length > 0 && (
        <ul>
          {suggestions.map(suggestion => (
            <li key={suggestion.id} onClick={() => handleSuggestionClick(suggestion)}>
              {suggestion.formatted}
            </li>
          ))}
        </ul>
      )}
    </form>
  );
}

export default GeocodingForm;
