import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import axios from 'axios';
import "./map.css"

mapboxgl.accessToken = 'pk.eyJ1IjoiemFpZGgiLCJhIjoiY2xjZDZwOThyMGF3aTN3cHU5bjhvNmgweiJ9.uoMkvDXyEXWsyDGDFrbgWQ';
      let timer;

export default function Map() {

    const mapContainer = useRef(null);
    const map = useRef(null);
    const [longitude, setLongitude] = useState(0);
    const [latitude, setLatitude] = useState(0);
    const [zoom, setZoom] = useState(4);

    useEffect(() => {
        map.current = new mapboxgl.Map({
          container: mapContainer.current,
          style: 'mapbox://styles/mapbox/streets-v12',
          center: [longitude, latitude],
          zoom: zoom
        });
      },[longitude, latitude,zoom]);

    const [isLoading, setIsLoading] = useState(false);
    const [city, setCity] = useState('');
    const [suggestions, setSuggestions] = useState([]);
 
  
     function handleSearch(event) {
      event.preventDefault();
      clearTimeout(timer);
      if(city.length === 0) {
        debugger
        setSuggestions([]);
        return;
      };
      timer = setTimeout(async () => {
      setIsLoading(true);
      const response = await axios.get(`https://api.locationiq.com/v1/autocomplete?key=pk.f0e243e14c2eef45faade1ad57332fb2&q=${city}`).catch((error)=> console.log(error));
       if(response.data.length===0)
       {
        setSuggestions(["No Results"])
       }
       else{
        var suggestions = response.data;
        setSuggestions(suggestions);
       }
        setIsLoading(false)
      }, 600);
     
    }
    useEffect(()=>{
      handleSearch()
    },[city])
  
    function handleSuggestionClick(suggestion) {
      setCity(suggestion.display_name)
      setLongitude(suggestion.lon);
      setLatitude(suggestion.lat);
      setZoom(10);
      setSuggestions([]);
    }
    console.log(suggestions)
      return (
    <div className="mainContainer">
    <div ref={mapContainer} className="map-container" />
    <form onSubmit={handleSearch}>
    <label htmlFor="city">City:</label><br />
    <input type="city" id="city" name="city" value={city} onChange={event => setCity(event.target.value)} placeholder="Enter a City" />
    <button type="submit">Search</button>
    {suggestions.length > 0 ? (
      <div className="suggestionList">
        <ul>
          {suggestions.map(suggestion => (
            <li key={suggestion.osm_id} onClick={() => handleSuggestionClick(suggestion)}>
              {suggestion.display_name}
            </li>
          ))}
        </ul>
        </div>
      ): isLoading? <span className="loader"></span>: <></>}
  
  </form>
  </div>
      );
}
