import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import axios from 'axios';
import "./map.css"

mapboxgl.accessToken = 'pk.eyJ1IjoiemFpZGgiLCJhIjoiY2xjZDZwOThyMGF3aTN3cHU5bjhvNmgweiJ9.uoMkvDXyEXWsyDGDFrbgWQ';
      let timer;

export default function Map() {

    const mapContainer = useRef(null);
    const map = useRef(null);
    const [longitude, setLongitude] = useState(-70.9);
    const [latitude, setLatitude] = useState(42.35);
    const [zoom, setZoom] = useState(4);

    useEffect(() => {
        map.current = new mapboxgl.Map({
          container: mapContainer.current,
          style: 'mapbox://styles/mapbox/streets-v12',
          center: [longitude, latitude],
          zoom: zoom
        });
      },[longitude, latitude,zoom]);

    const [isLoading, setIsLoading] = useState(false);
    const [city, setCity] = useState('');
    const [suggestions, setSuggestions] = useState([]);
 
  
     function handleSubmit(event) {
      event.preventDefault();
      clearTimeout(timer);
      timer = setTimeout(async () => {
      setIsLoading(true);
      const response = await axios.get('https://api.locationiq.com/v1/autocomplete?key=pk.f0e243e14c2eef45faade1ad57332fb2&q=',);
    
        console.log(response);
    
        setSuggestions(suggestions);
        setIsLoading(false)
      }, 500);
     
    }
  
    function handleSuggestionClick(suggestion) {
      setCity(suggestion.label)
      setLongitude(suggestion.longitude);
      setLatitude(suggestion.latitude);
      setZoom(10);
      setSuggestions([]);
    }
    console.log(longitude,latitude)
      return (
    <div className="mainContainer">
    <div ref={mapContainer} className="map-container" />
    <form onSubmit={handleSubmit}>
    <label htmlFor="city">City:</label><br />
    <input type="city" id="city" name="city" value={city} onChange={event => {setCity(event.target.value); handleSubmit(event);}} placeholder="Enter a City" />
    <button type="submit">Search</button>
    {suggestions.length > 0 ? (
      <div className="suggestionList">
        <ul>
          {suggestions.map(suggestion => (
            <li key={suggestion.latitude} onClick={() => handleSuggestionClick(suggestion)}>
              {suggestion.label}
            </li>
          ))}
        </ul>
        </div>
      ): isLoading? <span className="loader"></span>: <></>}
  
  </form>
  </div>
      );
}
